import { QueryApi } from '../../../../openapi/api';
export function clientConnections(sdk, clientID) {
    return new QueryApi(undefined, sdk.url).clientConnections(clientID);
}
export function connections(sdk, paginationKey, paginationOffset, paginationLimit, paginationCountTotal) {
    return new QueryApi(undefined, sdk.url).connections(paginationKey, paginationOffset?.toString(), paginationLimit?.toString(), paginationCountTotal);
}
export function clientState(sdk, connectionID) {
    return new QueryApi(undefined, sdk.url).connectionClientState(connectionID);
}
export function connectionConsensusState(sdk, connectionID, revisionNumber, revisionHeight) {
    return new QueryApi(undefined, sdk.url).connectionConsensusState(connectionID, revisionNumber.toString(), revisionHeight.toString());
}
